    private void botaoLogarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoLogarActionPerformed
        String login = campoLogin.getText();
        String senha = String.valueOf(campoSenha.getPassword());
        boolean logar = Conexao.conectLogin(login, senha);
        if(logar){
        Conexao.iniciarVariaveisProdutoEstoque();Conexao.iniciarVariaveisFornecedor();
        Conexao.iniciarVariaveisProdutoFornecedor();
        Conexao.iniciarVariaveisItemPedido();
        this.dispose(); new Principal().setVisible(true);
        }else{
            JOptionPane.showMessageDialog(null, "Login ou Senha incorretos!");
        }
    }//GEN-LAST:event_botaoLogarActionPerformed

